import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = ({ onBookingSaved }) => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    room_type: '',
    check_in: '',
    check_out: ''
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/bookings', form)
      .then(res => {
        onBookingSaved();
        setForm({ name: '', email: '', room_type: '', check_in: '', check_out: '' });
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" value={form.name} onChange={handleChange} placeholder="Nama" required />
      <input name="email" value={form.email} onChange={handleChange} placeholder="Email" required />
      <select name="room_type" value={form.room_type} onChange={handleChange} required>
        <option value="">Pilih Tipe Kamar</option>
        <option value="Single">Single</option>
        <option value="Double">Double</option>
        <option value="Suite">Suite</option>
      </select>
      <input type="date" name="check_in" value={form.check_in} onChange={handleChange} required />
      <input type="date" name="check_out" value={form.check_out} onChange={handleChange} required />
      <button type="submit">Pesan</button>
    </form>
  );
};

export default BookingForm;
