import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Rooms() {
  const [rooms, setRooms] = useState([]);
  const [roomName, setRoomName] = useState('');

  const fetchRooms = async () => {
    const res = await axios.get('/api/rooms');
    setRooms(res.data);
  };

  useEffect(() => { fetchRooms(); }, []);

  const addRoom = async () => {
    await axios.post('/api/rooms', { name: roomName });
    setRoomName('');
    fetchRooms();
  };

  const deleteRoom = async (id) => {
    await axios.delete(`/api/rooms/${id}`);
    fetchRooms();
  };

  return (
    <div>
      <h2>Daftar Kamar</h2>
      <input value={roomName} onChange={e => setRoomName(e.target.value)} placeholder="Nama kamar" />
      <button onClick={addRoom}>Tambah</button>
      <ul>
        {rooms.map(room => (
          <li key={room.id}>
            {room.name} <button onClick={() => deleteRoom(room.id)}>Hapus</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Rooms;
