import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Reservations() {
  const [rooms, setRooms] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState('');
  const [customerName, setCustomerName] = useState('');

  const fetchData = async () => {
    const res1 = await axios.get('/api/rooms');
    const res2 = await axios.get('/api/reservations');
    setRooms(res1.data);
    setReservations(res2.data);
  };

  useEffect(() => { fetchData(); }, []);

  const makeReservation = async () => {
    await axios.post('/api/reservations', { room_id: selectedRoom, customer: customerName });
    setCustomerName('');
    fetchData();
  };

  const deleteReservation = async (id) => {
    await axios.delete(`/api/reservations/${id}`);
    fetchData();
  };

  return (
    <div>
      <h2>Reservasi</h2>
      <select value={selectedRoom} onChange={e => setSelectedRoom(e.target.value)}>
        <option value="">Pilih Kamar</option>
        {rooms.map(room => (
          <option key={room.id} value={room.id}>{room.name}</option>
        ))}
      </select>
      <input value={customerName} onChange={e => setCustomerName(e.target.value)} placeholder="Nama Customer" />
      <button onClick={makeReservation}>Reservasi</button>
      <ul>
        {reservations.map(r => (
          <li key={r.id}>
            {r.customer} - {r.room_name} <button onClick={() => deleteReservation(r.id)}>Batal</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Reservations;
